import React, { useEffect, useState } from 'react';
import axios from 'axios';

// Definição de tipo para o usuário
interface User {
  id: number;
  name: string;
  email: string;
}

const App: React.FC = () => {
  // Estado para armazenar os usuários
  const [users, setUsers] = useState<User[]>([]);
  const [newUser, setNewUser] = useState<User>({ id: 0, name: '', email: '' });
  const [editUser, setEditUser] = useState<User | null>(null);

  // Carregar os usuários do backend
  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:8000/usuarios/');
      setUsers(response.data);
    } catch (error) {
      console.error('Erro ao buscar usuários', error);
    }
  };

  // Criar um novo usuário
  const createUser = async () => {
    try {
      await axios.post('http://127.0.0.1:8000/usuarios/', newUser);
      setNewUser({ id: 0, name: '', email: '' });  // Limpar o formulário
      fetchUsers();  // Atualizar a lista de usuários
    } catch (error) {
      console.error('Erro ao criar usuário', error);
    }
  };

  // Editar um usuário
  const updateUser = async () => {
    if (editUser) {
      try {
        await axios.put(`http://127.0.0.1:8000/usuarios/${editUser.id}`, editUser);
        setEditUser(null);  // Limpar a edição
        fetchUsers();  // Atualizar a lista de usuários
      } catch (error) {
        console.error('Erro ao editar usuário', error);
      }
    }
  };

  // Deletar um usuário
  const deleteUser = async (id: number) => {
    try {
      await axios.delete(`http://127.0.0.1:8000/usuarios/${id}`);
      fetchUsers();  // Atualizar a lista de usuários
    } catch (error) {
      console.error('Erro ao deletar usuário', error);
    }
  };

  // Efeito para carregar usuários ao iniciar o componente
  useEffect(() => {
    fetchUsers();
  }, []);

  return (
    <div>
      <h1>Usuários</h1>

      {/* Formulário para adicionar/editar usuário */}
      <div>
        <h2>{editUser ? 'Editar Usuário' : 'Novo Usuário'}</h2>
        <input
          type="text"
          placeholder="Nome"
          value={editUser ? editUser.name : newUser.name}
          onChange={(e) => (editUser ? setEditUser({ ...editUser, name: e.target.value }) : setNewUser({ ...newUser, name: e.target.value }))}
        />
        <input
          type="email"
          placeholder="Email"
          value={editUser ? editUser.email : newUser.email}
          onChange={(e) => (editUser ? setEditUser({ ...editUser, email: e.target.value }) : setNewUser({ ...newUser, email: e.target.value }))}
        />
        <button onClick={editUser ? updateUser : createUser}>
          {editUser ? 'Salvar Alterações' : 'Criar Usuário'}
        </button>
      </div>

      {/* Lista de usuários */}
      <div>
        <h2>Lista de Usuários</h2>
        <ul>
          {users.map((user) => (
            <li key={user.id}>
              {user.name} - {user.email}
              <button onClick={() => setEditUser(user)}>Editar</button>
              <button onClick={() => deleteUser(user.id)}>Deletar</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default App;
